import React from 'react';
import { IDockviewPanelProps } from 'dockview';

interface PanelParams {
  title: string;
  content: string;
  onPopout?: () => void;
  onMaximize?: () => void;
}

interface PanelContentProps extends IDockviewPanelProps {
  params: PanelParams;
}

const PanelContent: React.FC<PanelContentProps> = (props) => {
  const { title, content, onPopout, onMaximize } = props.params;

  return (
    <div style={{ padding: '20px', height: '100%', display: 'flex', flexDirection: 'column' }}>
      <div style={{ marginBottom: '15px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h3 style={{ margin: 0 }}>{title}</h3>
        <div style={{ display: 'flex', gap: '8px' }}>
          <button
            onClick={onPopout}
            style={{
              padding: '4px 8px',
              background: '#28a745',
              color: 'white',
              border: 'none',
              borderRadius: '3px',
              cursor: 'pointer',
              fontSize: '12px'
            }}
            title="Popout to separate window"
          >
            Popout
          </button>
          <button
            onClick={onMaximize}
            style={{
              padding: '4px 8px',
              background: '#ffc107',
              color: 'black',
              border: 'none',
              borderRadius: '3px',
              cursor: 'pointer',
              fontSize: '12px'
            }}
            title="Maximize panel"
          >
            Maximize
          </button>
        </div>
      </div>
      <div style={{ flex: 1, overflow: 'auto' }}>
        <p>{content}</p>
      </div>
    </div>
  );
};

export default PanelContent;
